from django.db import models# Create your models here.from django.utils.text import slugifyclass BaseModel(models.Model):    created_at = models.DateTimeField(auto_now_add=True)    updated_at = models.DateTimeField(auto_now=True)    class Meta:        abstract = Trueclass SlugBase(models.Model):    name = models.CharField(max_length=255, unique=True)    slug = models.SlugField(unique=True)        class Meta:        abstract = True    def save(self, *args, **kwargs):        if not self.slug:            self.slug = slugify(self.name)                    return super(SlugBase, self).save(*args, **kwargs)        def __str__(self):        return self.name