from rest_auth.serializers import UserDetailsSerializerfrom rest_framework import serializersclass BaseSerializer(serializers.ModelSerializer):    @property    def request(self):        return self.context.get('request')class UserSerializerMixin(BaseSerializer):    user = serializers.SerializerMethodField()    @staticmethod    def get_user(obj):        return UserDetailsSerializer(obj.user).data    @property    def validated_data(self):        data = super(UserSerializerMixin, self).validated_data        data['user'] = self.request.user        return data